sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	if(!g_form.getValue('from_date'))
		{
			alert("first you select from date");
			g_form.clearValue('to_date');

		}
	else{
		var sd = new Date(g_form.getValue('from_date'));


		var ed=new Date(g_form.getValue('to_date'));
		var count=0;
                while(sd.getTime()<=ed.getTime())
                {
					count++;
					sd.setDate(sd.getDate()+1);

                }

		g_form.setValue('no_of_days',count);
		}

   //Type appropriate comment here, and begin script below

}
